clear; clc; close all;

% 1) 物理参数与可调选项 -----------------------------------------
n0 = 1.0;                 % 入射介质折射率（空气）
n1 = 3.40;                % SiC在近/中红外的近似折射率（如有色散模型可替换）
angles_deg = [10, 15];    % 项目重点入射角
use_angle_for_surface = 10; % 上排3D图展示哪个入射角，默认10°

% 2) 扫描范围（聚焦到更相关区间） ------------------------------
lambda_nm = linspace(1800, 4800, 360);  % 波长范围 (nm)
d_um      = linspace(4, 12, 320);       % 厚度范围 (μm)，围绕最终推荐值~8.2 μm
d_nm      = d_um * 1000;

% 关键截面参数（结合README最终推荐值）
selected_d_um      = 8.2;     % 关键厚度 (μm)
selected_lambda_nm = 3000;    % 关键波长 (nm)

% 3) 网格与相位计算（3D图按use_angle_for_surface生成一套） ---------
theta_rad = deg2rad(use_angle_for_surface);
cos_theta2 = sqrt(1 - (n0/n1 * sin(theta_rad))^2);

[Lambda, Dnm] = meshgrid(lambda_nm, d_nm);  % Lambda: nm, Dnm: nm
OPD   = 2 * n1 * Dnm * cos_theta2;          % 光程差
delta = (2 * pi ./ Lambda) .* OPD;          % 相位差

% 两种边界相位模型（示意）：模型1无相位翻转；模型2含π相位翻转
Reflectivity_1 = 0.5 + 0.4 * cos(delta);       % 模型1: n1 < n2（示意）
Reflectivity_2 = 0.5 + 0.4 * cos(delta - pi);  % 模型2: n1 > n2（示意）

% 4) 关键截面索引 ---------------------------------------------------
[~, d_idx]      = min(abs(d_um - selected_d_um));
[~, lambda_idx] = min(abs(lambda_nm - selected_lambda_nm));

% 5) 为下排关键截面曲线，分别计算10°与15°两组 ----------------------
R1_thickness_all  = struct();  % 固定厚度：反射率-波长
R2_thickness_all  = struct();
R1_wavelength_all = struct();  % 固定波长：反射率-厚度
R2_wavelength_all = struct();

for a = angles_deg
th = deg2rad(a);
c2 = sqrt(1 - (n0/n1 * sin(th))^2);

% 重新用相同网格计算相位（只变入射角）
OPD_a   = 2 * n1 * Dnm * c2;
delta_a = (2 * pi ./ Lambda) .* OPD_a;

R1 = 0.5 + 0.4 * cos(delta_a);
R2 = 0.5 + 0.4 * cos(delta_a - pi);

key = sprintf('ang%d', a);
R1_thickness_all.(key)  = R1(d_idx, :);
R2_thickness_all.(key)  = R2(d_idx, :);
R1_wavelength_all.(key) = R1(:, lambda_idx);
R2_wavelength_all.(key) = R2(:, lambda_idx);
end

% 6) 绘图风格优化 ---------------------------------------------------
f = figure('Name','外延层干涉模型综合分析（优化版）',...
'Position',[100, 100, 1500, 1000], 'Color','w', ...
'Renderer','painters'); % 矢量友好

fontName = 'Helvetica'; % 若中文显示不全，可改为 'Microsoft YaHei'
fs_title = 13; fs_label = 12; fs_legend = 10;

% 优选色图（MATLAB R2020b+有turbo；否则退回parula）
try
cmap = turbo(256);
catch
cmap = parula(256);
end

% 子图1：模型1 3D曲面（use_angle_for_surface）
subplot(2,2,1);
surf(Lambda, d_um, Reflectivity_1, 'EdgeColor', 'none');
title(sprintf('模型1: n₀ < n₁ < n₂ | 3D视图（入射角 %d°）', use_angle_for_surface), ...
'FontSize', fs_title, 'FontWeight','bold','FontName',fontName);
xlabel('波长 λ (nm)','FontSize',fs_label,'FontName',fontName);
ylabel('厚度 d (μm)','FontSize',fs_label,'FontName',fontName);
zlabel('反射率 R','FontSize',fs_label,'FontName',fontName);
view(44, 28); colormap(cmap); cb1 = colorbar; cb1.Label.String = 'R'; grid on;
zlim([0.1 0.9]); ylim([min(d_um) max(d_um)]);
hold on;
% 标注关键截线
plot3(selected_lambda_nmones(size(d_um)), d_um, Reflectivity_1(:,lambda_idx), 'k--','LineWidth',1.2);
plot3(lambda_nm, selected_d_umones(size(lambda_nm)), Reflectivity_1(d_idx,:), 'k--','LineWidth',1.2);
hold off;

% 子图2：模型2 3D曲面（use_angle_for_surface）
subplot(2,2,2);
surf(Lambda, d_um, Reflectivity_2, 'EdgeColor', 'none');
title(sprintf('模型2: n₀ < n₁ > n₂ | 3D视图（入射角 %d°）', use_angle_for_surface), ...
'FontSize', fs_title, 'FontWeight','bold','FontName',fontName);
xlabel('波长 λ (nm)','FontSize',fs_label,'FontName',fontName);
ylabel('厚度 d (μm)','FontSize',fs_label,'FontName',fontName);
zlabel('反射率 R','FontSize',fs_label,'FontName',fontName);
view(44, 28); colormap(cmap); cb2 = colorbar; cb2.Label.String = 'R'; grid on;
zlim([0.1 0.9]); ylim([min(d_um) max(d_um)]);
hold on;
plot3(selected_lambda_nmones(size(d_um)), d_um, Reflectivity_2(:,lambda_idx), 'k--','LineWidth',1.2);
plot3(lambda_nm, selected_d_umones(size(lambda_nm)), Reflectivity_2(d_idx,:), 'k--','LineWidth',1.2);
hold off;

% 子图3：固定厚度的反射谱（叠加10°与15°，含两模型）
subplot(2,2,3);
hold on;
cols = lines(4);
plot(lambda_nm, R1_thickness_all.ang10, 'Color', cols(1,:), 'LineWidth', 2);
plot(lambda_nm, R2_thickness_all.ang10, 'Color', cols(2,:), 'LineStyle','--','LineWidth', 2);
plot(lambda_nm, R1_thickness_all.ang15, 'Color', cols(3,:), 'LineWidth', 2);
plot(lambda_nm, R2_thickness_all.ang15, 'Color', cols(4,:), 'LineStyle','--','LineWidth', 2);
xline(selected_lambda_nm, 'k:', 'LineWidth', 1.2);
scatter(selected_lambda_nm, R1_thickness_all.ang10(lambda_idx), 25, 'k', 'filled', 'MarkerFaceAlpha',0.7);
hold off; grid on;
title(sprintf('固定厚度 d = %.3f μm 的反射谱', selected_d_um), ...
'FontSize', fs_title, 'FontWeight','bold','FontName',fontName);
xlabel('波长 λ (nm)','FontSize',fs_label,'FontName',fontName);
ylabel('反射率 R','FontSize',fs_label,'FontName',fontName);
ylim([0.1 0.9]);
legend({'模型1@10°','模型2@10°','模型1@15°','模型2@15°'}, ...
'Location','best','FontSize',fs_legend,'Box','off');

% 子图4：固定波长的厚度响应（叠加10°与15°，含两模型）
subplot(2,2,4);
hold on;
plot(d_um, R1_wavelength_all.ang10, 'Color', cols(1,:), 'LineWidth', 2);
plot(d_um, R2_wavelength_all.ang10, 'Color', cols(2,:), 'LineStyle','--','LineWidth', 2);
plot(d_um, R1_wavelength_all.ang15, 'Color', cols(3,:), 'LineWidth', 2);
plot(d_um, R2_wavelength_all.ang15, 'Color', cols(4,:), 'LineStyle','--','LineWidth', 2);
xline(selected_d_um, 'k:', 'LineWidth', 1.2);
scatter(selected_d_um, R1_wavelength_all.ang10(d_idx), 25, 'k', 'filled', 'MarkerFaceAlpha',0.7);
hold off; grid on;
title(sprintf('固定波长 λ = %.0f nm 的厚度响应', selected_lambda_nm), ...
'FontSize', fs_title, 'FontWeight','bold','FontName',fontName);
xlabel('厚度 d (μm)','FontSize',fs_label,'FontName',fontName);
ylabel('反射率 R','FontSize',fs_label,'FontName',fontName);
ylim([0.1 0.9]);
legend({'模型1@10°','模型2@10°','模型1@15°','模型2@15°'}, ...
'Location','best','FontSize',fs_legend,'Box','off');

% 整体标题与脚注说明
sgtitle({ '外延层厚度干涉模型综合分析（SiC）', ...
sprintf('3D视图入射角=%d°；关键截面：d=%.3f μm，λ=%.0f nm', ...
use_angle_for_surface, selected_d_um, selected_lambda_nm) }, ...
'FontSize', 16, 'FontWeight','bold','FontName',fontName);

annotation('textbox', [0.12, 0.015, 0.76, 0.045], 'String', ...
['上排：3D全参数视图（左：模型1无相位翻转；右：模型2含π相位翻转） | ', ...
